css:
.image-view {
  display: flex;
  flex-direction: column;
  background-color: #e6eeef;
}
.image-view .image-controls {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 5px;
  border-bottom: 1px solid #b3cbd0;
  background-color: #e6eeef;
}
.image-view .image-controls a {
  display: inline-block;
  position: relative;
  width: 24px;
  height: 24px;
  margin: 5px;
  vertical-align: middle;
  font-size: 0;
  border-radius: 22px;
  border: 2px solid #fff;
  box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.4), 0 0 0 1px rgba(0, 0, 0, 0.1), 0 2px 2px rgba(0, 0, 0, 0.2);
  background-clip: content-box;
}
.image-view .image-controls a:hover {
  border-color: #ccc;
}
.image-view .image-controls a:active {
  border-color: #999;
}
.image-view .image-controls-color-white {
  color: #000;
  background-color: #fff;
  background-image: url("atom://image-view/images/transparent-background.png");
}
.image-view .image-controls-color-black {
  color: #fff;
  background-color: #000;
  background-image: url("atom://image-view/images/transparent-background.png");
}
.image-view .image-controls .btn-group {
  margin: 5px;
}
.image-view .image-controls .reset-zoom-button {
  min-width: 5em;
}
.image-view .image-container {
  flex: 1 1 0;
  display: flex;
  overflow: auto;
}
.image-view .image-container img {
  flex: none;
  margin: auto;
}
.image-view [background="white"] {
  background-color: white;
  background-image: url("atom://image-view/images/transparent-background.png");
}
.image-view [background="black"] {
  background-color: black;
  background-image: url("atom://image-view/images/transparent-background.png");
}
.image-view .zoom-to-fit.image-container {
  padding: 10px;
}
.image-view .zoom-to-fit.image-container img {
  flex: 1 1 0;
  margin: 0;
  object-fit: scale-down;
}
