css:
.btn {
  color: #424243;
  border-radius: 3px;
  border: none;
  text-shadow: none;
  height: 27px;
  line-height: 25px;
  padding: 0 10px;
  font-size: 12px;
  z-index: 0;
  background-color: #ffffff;
}
.btn:hover {
  background-color: #f5f5f5;
}
.btn.selected,
.btn.selected:hover {
  z-index: 1;
  background-color: #586dda;
}
.btn.btn-xs,
.btn-group-xs > .btn {
  padding: 2.5px 5px;
  font-size: 10px;
  height: auto;
  line-height: 1.3em;
}
.btn.btn-xs.icon:before,
.btn-group-xs > .btn.icon:before {
  font-size: 10px;
}
.btn.btn-sm,
.btn-group-sm > .btn {
  padding: 2.5px 5px;
  height: auto;
  line-height: 1.3em;
}
.btn.btn-sm.icon:before,
.btn-group-sm > .btn.icon:before {
  font-size: 13px;
}
.btn.btn-lg,
.btn-group-lg > .btn {
  font-size: 14px;
  padding: 8px 12px;
  height: auto;
  line-height: 1.3em;
}
.btn.btn-lg.icon:before,
.btn-group-lg > .btn.icon:before {
  font-size: 18px;
}
.btn-group > .btn {
  border-left: 1px solid #d1d1d2;
  border-right: 1px solid #d1d1d2;
}
.btn-group > .btn:first-child {
  border-left: none;
  border-top-left-radius: 3px;
  border-bottom-left-radius: 3px;
}
.btn-group > .btn:last-child,
.btn-group > .btn.selected:last-child,
.btn-group > .dropdown-toggle {
  border-right: none;
  border-top-right-radius: 3px;
  border-bottom-right-radius: 3px;
}
.btn.icon:before {
  width: initial;
  height: initial;
  margin-right: .3125em;
}
.btn.icon:empty:before {
  margin-right: 0;
}
.btn-toolbar > .btn-group + .btn-group,
.btn-toolbar > .btn-group + .btn,
.btn-toolbar > .btn + .btn {
  float: none;
  display: inline-block;
  margin-left: 0;
}
.btn-toolbar > * {
  margin-right: 5px;
}
.btn-toolbar > *:last-child {
  margin-right: 0;
}
