css:
.panel {
  border-radius: 0;
  border: none;
  background-color: transparent;
  box-shadow: none;
}
.panel.bordered .panel-heading {
  border-radius: 3px 3px 0 0;
}
.inset-panel .panel-heading {
  border-radius: 3px 3px 0 0;
}
.panel-heading {
  margin: 0;
  padding: 10px;
  border-radius: 0;
  font-size: 12px;
  line-height: 1;
}
.panel-heading .btn {
  height: 20px;
  line-height: 20px;
  font-size: 10px;
  position: relative;
  top: -5px;
}
atom-panel-container.left,
atom-panel-container.right {
  display: flex;
}
.tool-panel,
atom-panel {
  display: block;
  position: relative;
  background-color: #e5e5e6;
}
atom-panel-container > atom-panel.left,
atom-panel-container > atom-panel.right {
  display: flex;
}
atom-panel-container > atom-panel.left > *,
atom-panel-container > atom-panel.right > * {
  height: initial;
}
.overlay,
atom-panel.modal {
  position: absolute;
  display: block;
  left: 50%;
  width: 500px;
  margin-left: -250px;
  z-index: 9999;
  box-sizing: border-box;
  color: #424243;
  background-color: #fafafa;
  padding: 10px;
}
.overlay h1,
atom-panel.modal h1 {
  margin-top: 0;
  color: #282929;
  font-size: 1.6em;
  font-weight: bold;
}
.overlay h2,
atom-panel.modal h2 {
  font-size: 1.3em;
}
.overlay atom-text-editor[mini],
atom-panel.modal atom-text-editor[mini] {
  margin-bottom: 10px;
}
.overlay .message,
atom-panel.modal .message {
  padding-top: 5px;
  font-size: 11px;
}
.overlay.mini,
atom-panel.modal.mini {
  width: 200px;
  margin-left: -100px;
  font-size: 12px;
}
.overlay.from-top,
atom-panel.modal {
  top: 0;
  border-top: none;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}
.overlay.from-bottom {
  bottom: 0;
  border-bottom: none;
  border-bottom-left-radius: 0;
  border-bottom-right-radius: 0;
}
.overlay.floating {
  left: auto;
}
